import java.util.*;public class Ucenec {		private static final int KROZEK_CAS_IZVAJANJA = 2;		private static int obremenitev;	private List<Krozek> krozki;		private String name;	private String surrname;		public static void nastaviMaksObremenitev(int maksObremenitev) {		obremenitev = maksObremenitev;	}		public Ucenec(String ime, String priimek) {		this.name = ime;		this.surrname = priimek;				krozki = new ArrayList<>();	}		public String vrniIP() {		return String.format("%s %s", name, surrname);	}		public boolean vclani(Krozek krozek) {		if(krozki.contains(krozek)) return true;		if(!canSignUp(this, krozek)) return false;			krozki.add(krozek);			krozek.getMembers().add(this);				return true;	}		private boolean canSignUp(Ucenec student, Krozek krozek) {		if(steviloKrozkov() == obremenitev) return false;		if(krozek.getCapacity() == krozek.steviloClanov()) return false;				for(Krozek k : krozki) {			if(k.getDay() != krozek.getDay()) continue;  			if(Math.abs(k.getHour() - krozek.getHour()) < KROZEK_CAS_IZVAJANJA) return false;		}				return true;	}		public void izclani(Krozek krozek) {		krozki.remove(krozek);		krozek.getMembers().remove(this);	}	public int steviloKrozkov() {		return krozki.size();	}}